import React, { useState } from "react";
import axios from "axios";
import "./taxi.css";

function Taxi() {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        phone: "",
        pickup: "",
        destination: "",
        date: "",
        time: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:5000/bookings", formData);
            alert("Booking successful!");
            setFormData({
                name: "",
                email: "",
                phone: "",
                pickup: "",
                destination: "",
                date: "",
                time: "",
            });
        } catch (err) {
            console.error("Error submitting form:", err);
            alert("Error submitting booking. Please try again.");
        }
    };

    return (
        <div>
            {/* Navigation Bar */}
            <nav className="navbar">
                <h4>Taxi Booking Site</h4>
                <a href="#home">Home</a>
                <a href="#places">Popular Cities</a>
                <a href="#about">About Us</a>
                <a href="#book-taxi">Book a Taxi</a>
            </nav>

            {/* Home Section */}
            <section id="home">
                <h2>Ride With Us...</h2>
                <p>Your trusted partner for safe, reliable, and affordable taxi services.</p>
                <p>
                    Book your ride in just a few clicks and experience unmatched comfort with our professional drivers and
                    well-maintained vehicles.
                </p>
            </section>

            {/* Popular Cities Section */}
            <div className="container">
                <section id="places" className="city">
                    <h2>Popular Cities</h2>
                    <div className="images">
                        <img src="images/chennai.jfif" alt="Chennai" />
                        <img src="images/coimbatore.jfif" alt="Coimbatore" />
                        <img src="images/kanyakumari.jfif" alt="Kanyakumari" />
                        <img src="images/yercaud.jfif" alt="Yercaud" />
                        <img src="images/kerala.jfif" alt="Kerala" />
                        <img src="images/tenkasi.jfif" alt="Tenkasi" />
                    </div>
                </section>
            </div>

            {/* About Us Section */}
            <section id="about">
                <h2>About Us</h2>
                <p>
                    At Taxi Booking Site, we pride ourselves on delivering top-notch travel experiences for our
                    customers. Established in [Year], we have become a trusted name in the transportation industry.
                </p>
                <p>
                    Whether youâ€™re commuting within the city, heading out on a road trip, or visiting new destinations,
                    our services are tailored to meet your needs.
                </p>
            </section>

            {/* Book a Taxi Section */}
            <section id="book-taxi">
                <h2>Book a Taxi</h2>
                <div className="form-container">
                    <form id="taxiForm" onSubmit={handleSubmit}>
                        <label htmlFor="name">Full Name</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            className="form-control"
                            placeholder="Enter your full name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="email">Email Address</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            className="form-control"
                            placeholder="Enter your email address"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="phone">Phone Number</label>
                        <input
                            type="tel"
                            id="phone"
                            name="phone"
                            className="form-control"
                            placeholder="Enter your phone number"
                            value={formData.phone}
                            onChange={handleChange}
                            required
                            pattern="[0-9]{10}"
                        />

                        <label htmlFor="pickup">Pickup Location</label>
                        <input
                            type="text"
                            id="pickup"
                            name="pickup"
                            className="form-control"
                            placeholder="Enter pickup location"
                            value={formData.pickup}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="destination">Destination</label>
                        <input
                            type="text"
                            id="destination"
                            name="destination"
                            className="form-control"
                            placeholder="Enter destination"
                            value={formData.destination}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="date">Pickup Date</label>
                        <input
                            type="date"
                            id="date"
                            name="date"
                            className="form-control"
                            value={formData.date}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="time">Pickup Time</label>
                        <input
                            type="time"
                            id="time"
                            name="time"
                            className="form-control"
                            value={formData.time}
                            onChange={handleChange}
                            required
                        />

                        <button type="submit" className="btn btn-primary w-100">
                            Book Taxi
                        </button>
                    </form>
                </div>
            </section>

            {/* Success Popup */}
            {success && (
                <div className="success-popup">
                    <img src="images/tick.png" alt="Success" className="success-icon" />
                    <p>Booking Successful!</p>
                </div>
            )}

            {/* Footer Section */}
            <footer className="footer">
                <p>&copy; 2024 Responsive Webpage. All rights reserved.</p>
            </footer>
        </div>
    );
}

export default Taxi;
