import React, { useState } from "react";
import axios from "axios";
import "./taxi.css";

function Taxi() {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        phone: "",
        pickup: "",
        destination: "",
        date: "",
        time: "",
    });

    const [success, setSuccess] = useState(false); // To track success message visibility

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:5000/bookings", formData);
            setSuccess(true); // Show success message
            setFormData({
                name: "",
                email: "",
                phone: "",
                pickup: "",
                destination: "",
                date: "",
                time: "",
            });

            // Hide success message after 2 seconds
            setTimeout(() => {
                setSuccess(false);
            }, 2000);
        } catch (err) {
            console.error("Error submitting form:", err);
            alert("Error submitting booking. Please try again.");
        }
    };

    return (
        <div>
            {/* Navigation Bar */}
            <nav className="navbar">
                <h4>Taxi Booking Site</h4>
                <a href="#home">Home</a>
                <a href="#places">Popular Cities</a>
                <a href="#about">About Us</a>
                <a href="#book-taxi">Book a Taxi</a>
            </nav>

            {/* Home Section */}
            <section id="home">
                <h2>Ride With Us...</h2>
                <p>Your trusted partner for safe, reliable, and affordable taxi services.</p>
            </section>

            {/* Book a Taxi Section */}
            <section id="book-taxi">
                <h2>Book a Taxi</h2>
                <div className="form-container">
                    <form id="taxiForm" onSubmit={handleSubmit}>
                        <label htmlFor="name">Full Name</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            className="form-control"
                            placeholder="Enter your full name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="email">Email Address</label>
                        <input
                            type="email"
                            id="email"
                            name="email"
                            className="form-control"
                            placeholder="Enter your email address"
                            value={formData.email}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="phone">Phone Number</label>
                        <input
                            type="tel"
                            id="phone"
                            name="phone"
                            className="form-control"
                            placeholder="Enter your phone number"
                            value={formData.phone}
                            onChange={handleChange}
                            required
                            pattern="[0-9]{10}"
                        />

                        <label htmlFor="pickup">Pickup Location</label>
                        <input
                            type="text"
                            id="pickup"
                            name="pickup"
                            className="form-control"
                            placeholder="Enter pickup location"
                            value={formData.pickup}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="destination">Destination</label>
                        <input
                            type="text"
                            id="destination"
                            name="destination"
                            className="form-control"
                            placeholder="Enter destination"
                            value={formData.destination}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="date">Pickup Date</label>
                        <input
                            type="date"
                            id="date"
                            name="date"
                            className="form-control"
                            value={formData.date}
                            onChange={handleChange}
                            required
                        />

                        <label htmlFor="time">Pickup Time</label>
                        <input
                            type="time"
                            id="time"
                            name="time"
                            className="form-control"
                            value={formData.time}
                            onChange={handleChange}
                            required
                        />

                        <button type="submit" className="btn btn-primary w-100">
                            Book Taxi
                        </button>
                    </form>
                </div>
            </section>

            {/* Success Popup */}
            {success && (
                <div className="success-popup">
                    <img src="images/tick.png" alt="Success" className="success-icon" />
                    <p>Booking Successful!</p>
                </div>
            )}

            {/* Footer Section */}
            <footer className="footer">
                <p>&copy; 2024 Responsive Webpage. All rights reserved.</p>
            </footer>
        </div>
    );
}

export default Taxi;
