import React, { useState } from "react";
import axios from "axios";
import "./taxi.css";

function Taxi() {
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        phone: "",
        pickup: "",
        destination: "",
        date: "",
        time: "",
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post("http://localhost:5000/bookings", formData);
            alert("Booking successful!");
            setFormData({
                name: "",
                email: "",
                phone: "",
                pickup: "",
                destination: "",
                date: "",
                time: "",
            });
        } catch (err) {
            console.error("Error submitting form:", err);
            alert("Error submitting booking. Please try again.");
        }
    };

    return (
        <div>
            {/* Your existing code */}
            <section id="book-taxi">
                <h2>Book a Taxi</h2>
                <div className="form-container">
                    <form id="taxiForm" onSubmit={handleSubmit}>
                        <label htmlFor="name">Full Name</label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            className="form-control"
                            placeholder="Enter your full name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                        />
                        {/* Add the rest of the form fields similarly */}
                        <button type="submit" name="insert" className="btn btn-primary w-100">
                            Book Taxi
                        </button>
                    </form>
                </div>
            </section>
            {/* Your existing footer */}
        </div>
    );
}

export default Taxi;
